create VIEW getcourse as select `c`.`id` AS `course_id`,`c`.`youtube_into` AS `youtube_video`,`c`.`terms` AS `terms`,`cl`.`class_name` AS `class_name`,`cl`.`id` AS `classid`,`cc`.`title` AS `subjects`,`cc`.`file` AS `subject_thumb`,`u`.`name` AS `trainerName`,`u`.`image` AS `trainerImage`,`c`.`post_title` AS `courseTitle`,`c`.`price` AS `price`,`c`.`thumbnail` AS `thumbnail` from ((( `course` `c` join  `course_categories` `cc` on(`cc`.`id` = `c`.`course_category_id`)) join  `users` `u` on(`u`.`subject_id` = `c`.`course_category_id`)) join  `classes` `cl` on(`cl`.`id` = `c`.`class_id`))

CREATE VIEW getsinglecourse as select `c`.`id` AS `course_id`,`cl`.`class_name` AS `class_name`,`cl`.`id` AS `classid`,`cc`.`title` AS `subjects`,`c`.`post_title` AS `courseTitle`,`c`.`price` AS `price`,`c`.`short_description` AS `short_description`,`c`.`terms` AS `terms`,`c`.`post_description` AS `post_description`,`c`.`created_on` AS `created_on`,`c`.`video_url` AS `video_url`,`c`.`thumbnail` AS `thumbnail`,`u`.`name` AS `trainerName`,`u`.`image` AS `trainerImage`,`u`.`designation` AS `trainerDesignation`,`u`.`description` AS `trainerDescription`,(select count( `course_lectures`.`id`) from  `course_lectures` where  `course_lectures`.`course_id` = `c`.`id`) AS `lesseonsCount` from ((( `course` `c` join  `course_categories` `cc` on(`cc`.`id` = `c`.`course_category_id`)) join  `users` `u` on(`u`.`subject_id` = `c`.`course_category_id`)) join  `classes` `cl` on(`cl`.`id` = `c`.`class_id`))


CREATE VIEW subjects AS select  `course_categories`.`id` AS `id`, `course_categories`.`title` AS `title`, `course_categories`.`icon_class` AS `icon_class`, `course_categories`.`class_id` AS `class_id` from  `course_categories`